import React, { useState } from 'react';
import ExpensesFilter from './ExpensesFilter';
import ExpensesList from './ExpensesList'; // A component to display filtered expenses
import { DUMMY_EXPENSES } from '../'; // Import the expenses array

const Expenses = () => {
  const [filteredYear, setFilteredYear] = useState(new Date().getFullYear().toString());
  const [filteredMonth, setFilteredMonth] = useState('');

  const filterChangeHandler = (selectedYear) => {
    setFilteredYear(selectedYear);
  };

  const monthChangeHandler = (selectedMonth) => {
    setFilteredMonth(selectedMonth);
  };

  const filteredExpenses = DUMMY_EXPENSES.filter((expense) => {
    const matchesYear = expense.date.getFullYear().toString() === filteredYear;
    const matchesMonth =
      filteredMonth === '' || expense.date.getMonth().toString() === filteredMonth;
    return matchesYear && matchesMonth;
  });

  return (
    <div>
      <ExpensesFilter
        selectedYear={filteredYear}
        selectedMonth={filteredMonth}
        onChangeFilter={filterChangeHandler}
        onChangeMonth={monthChangeHandler}
      />
      <ExpensesList items={filteredExpenses} />
    </div>
  );
};

export default Expenses;
